import React, { useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";


function UserDashboard({ username }) {
  const [showLogSection, setShowLogSection] = useState(false);
  const [barcode, setBarcode] = useState("");
  const [itemInfo, setItemInfo] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [room, setRoom] = useState("");
  const [clientName, setClientName] = useState("");
  const [statusMessage, setStatusMessage] = useState("");

  <TopBar />

  const handleBarcodeSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.get(`/api/items/${barcode}`);
      const item = res.data;

      setItemInfo(item);

      if (item.status === "checked out") {
        // Automatically check in
        await axios.post(`/api/items/check-in`, {
          barcode,
          username,
        });
        setStatusMessage("Item checked in");
        setBarcode("");
      } else {
        // Open modal for check-out
        setModalOpen(true);
      }
    } catch (error) {
      setStatusMessage("Item not found in database.");
      console.error(error);
    }
  };

  const handleCheckOut = async () => {
    try {
      await axios.post(`/api/items/check-out`, {
        barcode,
        username,
        room,
        clientName,
      });
      setStatusMessage("Item checked out");
      setModalOpen(false);
      setBarcode("");
      setRoom("");
      setClientName("");
    } catch (error) {
      setStatusMessage("Failed to check out item.");
      console.error(error);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-4">Welcome to the User Dashboard</h2>
      <button
        className="bg-blue-600 text-white px-4 py-2 rounded"
        onClick={() => setShowLogSection(!showLogSection)}
      >
        Log Items
      </button>

      {showLogSection && (
        <form onSubmit={handleBarcodeSubmit} className="mt-4">
          <label className="block mb-2">Scan or Enter Barcode:</label>
          <input
            type="text"
            value={barcode}
            onChange={(e) => setBarcode(e.target.value)}
            className="border border-gray-400 px-3 py-2 rounded w-full"
            autoFocus
          />
        </form>
      )}

      {statusMessage && <p className="mt-4 font-medium">{statusMessage}</p>}

      {/* Modal */}
      {modalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-md w-96">
            <h3 className="text-xl font-bold mb-4">Check Out Item</h3>
            <p className="mb-2">Item: {itemInfo?.name}</p>
            <label className="block mb-1">Room:</label>
            <input
              type="text"
              value={room}
              onChange={(e) => setRoom(e.target.value)}
              className="border border-gray-300 w-full p-2 mb-3 rounded"
            />
            <label className="block mb-1">Client Name:</label>
            <input
              type="text"
              value={clientName}
              onChange={(e) => setClientName(e.target.value)}
              className="border border-gray-300 w-full p-2 mb-3 rounded"
            />
            <div className="flex justify-end space-x-2">
              <button
                onClick={() => setModalOpen(false)}
                className="px-4 py-2 bg-gray-400 text-white rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleCheckOut}
                className="px-4 py-2 bg-green-600 text-white rounded"
              >
                Done
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
