// src/pages/AdminLookupCode.jsx
import React, { useState } from "react";
import axios from "axios";

function AdminLookupCode() {
  const [searchTerm, setSearchTerm] = useState("");
  const [items, setItems] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const searchItem = async () => {
    setLoading(true);
    setItems([]);
    setError("");

    try {
      // Try exact barcode match first
      const barcodeRes = await axios.get(`/api/items/${searchTerm}`);
      if (barcodeRes.data) {
        setItems([barcodeRes.data]);
        setLoading(false);
        return;
      }
    } catch (err) {
      // Barcode match failed — fall back to fuzzy name search
      try {
        const nameRes = await axios.get(`/api/items?name=${encodeURIComponent(searchTerm)}`);
        if (Array.isArray(nameRes.data) && nameRes.data.length > 0) {
          setItems(nameRes.data);
          setLoading(false);
          return;
        }
      } catch (innerErr) {
        // Fallthrough to final error handler
      }
    }

    setError("Item not found");
    setLoading(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") searchItem();
  };

  return (
    <div className="p-6 text-white">
      <h2 className="text-2xl font-bold mb-4 text-yellow-400">Lookup Barcode / QR Code</h2>
      <div className="flex gap-3 mb-6">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Enter barcode or item name"
          className="bg-gray-800 px-4 py-2 rounded w-80"
        />
        <button
          onClick={searchItem}
          className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700"
        >
          Search
        </button>
      </div>

      {loading && <p>Searching...</p>}
      {error && <p className="text-red-400">{error}</p>}

      {items.length > 0 && (
        <div className="grid gap-4 grid-cols-1 sm:grid-cols-2">
          {items.map((item) => (
            <div
              key={item._id || item.barcode}
              className="bg-gray-800 p-4 rounded shadow max-w-md"
            >
              <p className="text-lg font-semibold mb-2">Item: {item.name}</p>
              <p className="mb-1">Barcode: <code>{item.barcode}</code></p>

              {item.category?.toLowerCase() === "mic" ? (
                <img
                  src={`https://api.qrserver.com/v1/create-qr-code/?data=${item.barcode}&size=150x150`}
                  alt="QR Code"
                  className="mx-auto mt-4"
                />
              ) : (
                <div className="mt-4">
                  <p className="text-sm">(Text only barcode)</p>
                  <button
                    onClick={() => navigator.clipboard.writeText(item.barcode)}
                    className="text-blue-400 underline text-sm mt-1"
                  >
                    Copy to clipboard
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AdminLookupCode;
