// ItemStatus.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import dayjs from "dayjs";
import duration from "dayjs/plugin/duration";
import TopBar from "../components/TopBar";

dayjs.extend(duration);

function ItemStatus() {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/items");
      setItems(res.data);
    } catch (err) {
      console.error("Failed to fetch items", err);
    }
  };

  const categorizeItems = () => {
    const checkedOut = items.filter(i => i.checkedOutBy);
    const missing = items.filter(i => i.isMissing);
    const broken = items.filter(i => i.isBroken);
    return { checkedOut, missing, broken };
  };

  const getLatestLog = (item) => {
    return item.logs?.slice().reverse().find(log => log.action === "check_out") || null;
  };

  const timeSinceCheckout = (timestamp) => {
    const now = dayjs();
    const then = dayjs(timestamp);
    const diffHours = now.diff(then, 'hour');
    return {
      text: `${now.to(then, true)} ago`,
      color: diffHours > 12 ? "text-red-500" : "text-white",
    };
  };

  const handleAction = async (item, action) => {
    let updates = {};
    const now = new Date();
    const user = "Admin"; // optionally pull from context

    if (action === "checkin") {
      updates = {
        isAvailable: true,
        checkedOutBy: null,
        $push: {
          logs: {
            action: "check_in",
            user,
            timestamp: now,
          },
        },
      };
    } else if (action === "lost") {
      updates = { isMissing: true };
    } else if (action === "broken") {
      updates = { isBroken: true };
    } else if (action === "found") {
      updates = { isMissing: false };
    } else if (action === "fixed") {
      updates = { isBroken: false };
    }

    try {
      await axios.put(`http://localhost:5000/api/items/${item._id}`, updates);
      fetchItems();
      setModalOpen(false);
    } catch (err) {
      console.error("Action failed", err);
    }
  };

  const renderItemCard = (item) => (
    <div
      key={item._id}
      className="bg-gray-800 p-4 rounded shadow cursor-pointer hover:bg-gray-700"
      onClick={() => {
        setSelectedItem(item);
        setModalOpen(true);
      }}
    >
      <h3 className="text-lg font-bold">{item.name}</h3>
      <p className="text-sm">Barcode: {item.barcode}</p>
    </div>
  );

  const renderModal = () => {
    if (!selectedItem) return null;
    const latestLog = getLatestLog(selectedItem);
    const time = latestLog?.timestamp ? timeSinceCheckout(latestLog.timestamp) : null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center z-50">
        <div className="bg-gray-900 p-6 rounded-lg w-full max-w-lg">
          <h2 className="text-xl font-bold mb-4">{selectedItem.name}</h2>
          {latestLog && (
            <div className="mb-4">
              <p>Client: {latestLog.clientName}</p>
              <p>Room: {latestLog.room}</p>
              <p>Event #: {latestLog.eventNumber}</p>
              <p>User: {latestLog.user}</p>
              <p>Date: {dayjs(latestLog.timestamp).format("MMM D, YYYY h:mm A")}</p>
              <p className={`font-bold ${time.color}`}>Time Out: {time.text}</p>
            </div>
          )}

          <div className="flex gap-2 flex-wrap">
            {selectedItem.checkedOutBy && (
              <>
                <button onClick={() => handleAction(selectedItem, "checkin")} className="bg-green-600 px-4 py-2 rounded">Mark as Checked In</button>
                <button onClick={() => handleAction(selectedItem, "lost")} className="bg-yellow-500 px-4 py-2 rounded">Mark as Lost</button>
                <button onClick={() => handleAction(selectedItem, "broken")} className="bg-red-600 px-4 py-2 rounded">Mark as Broken</button>
              </>
            )}
            {selectedItem.isMissing && (
              <button onClick={() => handleAction(selectedItem, "found")} className="bg-blue-500 px-4 py-2 rounded">Mark as Found</button>
            )}
            {selectedItem.isBroken && (
              <button onClick={() => handleAction(selectedItem, "fixed")} className="bg-indigo-500 px-4 py-2 rounded">Mark as Fixed</button>
            )}
            <button onClick={() => setModalOpen(false)} className="bg-gray-600 px-4 py-2 rounded ml-auto">Close</button>
          </div>
        </div>
      </div>
    );
  };

  const { checkedOut, missing, broken } = categorizeItems();

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white p-6">
        <h1 className="text-3xl font-bold mb-6">Item Status Overview</h1>

        <div className="mb-10">
          <h2 className="text-2xl font-semibold mb-4">Checked Out</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {checkedOut.map(renderItemCard)}
          </div>
        </div>

        <div className="mb-10">
          <h2 className="text-2xl font-semibold mb-4">Missing</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {missing.map(renderItemCard)}
          </div>
        </div>

        <div className="mb-10">
          <h2 className="text-2xl font-semibold mb-4">Broken</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {broken.map(renderItemCard)}
          </div>
        </div>
      </div>

      {modalOpen && renderModal()}
    </>
  );
}

export default ItemStatus;
