import React, { useEffect, useState } from "react";
import axios from "axios";

function ManageItems() {
  const [items, setItems] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    barcode: "",
    building: "Memorial Union",
    category: "Mic",
  });
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [status, setStatus] = useState("");

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/items");
      setItems(res.data);
    } catch (err) {
      console.error("❌ Failed to fetch items", err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");

    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/api/items/${editingId}`, formData);
      } else {
        await axios.post("http://localhost:5000/api/items", formData);
      }

      fetchItems();
      setShowModal(false);
      resetForm();
    } catch (err) {
      if (err.response?.status === 400 && err.response.data?.message) {
        setStatus(err.response.data.message);
      } else {
        setStatus("❌ Failed to save item");
      }
    }
  };

  const handleEdit = (item) => {
    setFormData(item);
    setEditingId(item._id);
    setStatus("");
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/items/${id}`);
      fetchItems();
    } catch (err) {
      console.error("❌ Failed to delete item", err);
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      barcode: "",
      building: "Memorial Union",
      category: "Mic",
    });
    setEditingId(null);
    setStatus("");
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Item Management</h1>
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
        >
          + Add Item
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {items.map((item) => (
          <div key={item._id} className="bg-gray-800 p-4 rounded-xl shadow">
            <h2 className="text-xl font-semibold">{item.name}</h2>
            <p className="text-sm">Barcode: {item.barcode}</p>
            <p className="text-sm">Building: {item.building}</p>
            <p className="text-sm">Category: {item.category}</p>
            <div className="flex gap-2 mt-2">
              <button onClick={() => handleEdit(item)} className="bg-yellow-500 px-3 py-1 rounded">
                Edit
              </button>
              <button onClick={() => handleDelete(item._id)} className="bg-red-600 px-3 py-1 rounded">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-xl max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">{editingId ? "Edit Item" : "Add Item"}</h2>
            {status && <p className="text-red-400 text-sm mb-2">{status}</p>}
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Item Name"
                className="w-full p-2 bg-gray-700 rounded"
                required
              />
              <input
                name="barcode"
                value={formData.barcode}
                onChange={handleChange}
                placeholder="Barcode"
                className="w-full p-2 bg-gray-700 rounded"
                required
              />
              <select
                name="building"
                value={formData.building}
                onChange={handleChange}
                className="w-full p-2 bg-gray-700 rounded"
              >
                <option>Memorial Union</option>
                <option>Student Pavilion</option>
              </select>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="w-full p-2 bg-gray-700 rounded"
              >
                <option>Mic</option>
                <option>Lavaliers</option>
                <option>HDMI Cable</option>
                <option>Tech Bag</option>
                <option>Clicker</option>
              </select>
              <div className="flex justify-end gap-4">
                <button type="submit" className="bg-green-600 px-4 py-2 rounded">
                  Save
                </button>
                <button type="button" onClick={() => setShowModal(false)} className="bg-gray-500 px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ManageItems;
