import React, { useEffect, useState } from "react";
import axios from "axios";
import moment from "moment";

function ScanPage() {
  const [barcode, setBarcode] = useState("");
  const [item, setItem] = useState(null);
  const [techBagVerification, setTechBagVerification] = useState({});
  const [mallChairBarcodes, setMallChairBarcodes] = useState(["", ""]);
  const [formFields, setFormFields] = useState({
    arrivalTime: moment().format("HH:mm"),
    arrivalStatus: "On-Time",
    org: "",
    tablingSpot: "",
    eventNumber: "",
    chairCount: 0,
  });
  const [feedbackModal, setFeedbackModal] = useState({ show: false, message: "", color: "" });
const [modalOpen, setModalOpen] = useState(true); // or false if closed by default

// Optional sound effect function
const playSound = (success) => {
  const audio = new Audio(success ? "/sounds/success.mp3" : "/sounds/fail.mp3");
  audio.play();
};


  const handleScan = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/items?barcode=${barcode}`);
      const fetchedItem = res.data[0]; // assuming unique barcodes
      setItem(fetchedItem);

      if (fetchedItem.category === "Tech Bag") {
        const contents = fetchedItem.techBagContents || {};
        setTechBagVerification({
          [contents?.hdmiCable?.barcode]: false,
          [contents?.clicker?.barcode]: false,
          [contents?.adapter?.barcode]: false,
        });
      }
    } catch (err) {
      console.error("Item not found", err);
    }
  };

  const handleCheckOut = async () => {
    try {
      const payload = {
        barcode,
        username: user.username,
        room,
        clientName,
        eventNumber,
      };

      if (item.category === "Tech Bag") {
        payload.techBagVerification = techBagVerification;
      }

      if (item.category === "Mall Table") {
        payload.mallChairBarcodes = mallChairBarcodes.filter(Boolean);
      }

      await axios.post(`/api/items/check-out`, payload);
      setFeedbackModal({ show: true, message: "Item checked out", color: "green" });
      playSound(true);

      // Reset state
      setModalOpen(false);
      setBarcode("");
      setItem(null);
      setRoom("");
      setClientName("");
      setEventNumber("");
      setTechBagVerification({});
      setMallChairBarcodes(["", ""]);
    } catch (error) {
      setFeedbackModal({ show: true, message: error.response?.data?.message || "Failed to check out item", color: "red" });
      playSound(false);
      console.error(error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-4">Item Scan & Check-Out</h1>

      <div className="mb-6">
        <input
          className="bg-gray-800 p-2 rounded w-full"
          placeholder="Scan Item Barcode"
          value={barcode}
          onChange={(e) => setBarcode(e.target.value)}
        />
        <button
          onClick={handleScan}
          className="mt-2 bg-blue-600 px-4 py-2 rounded"
        >
          Fetch Item
        </button>
      </div>

      {item && (
        <div className="bg-gray-800 p-4 rounded-xl">
          <h2 className="text-xl font-semibold mb-2">{item.name}</h2>
          <p>Barcode: {item.barcode}</p>
          <p>Category: {item.category}</p>

          {/* Tech Bag Verification */}
          {item.category === "Tech Bag" && (
            <div className="mt-4 space-y-2">
              <h3 className="text-lg font-bold">Verify Contents</h3>
              {Object.entries(techBagVerification).map(([barcode, verified]) => (
                <div key={barcode} className="flex items-center gap-2">
                  <label>{barcode}</label>
                  <input
                    type="checkbox"
                    checked={verified}
                    onChange={() =>
                      setTechBagVerification((prev) => ({
                        ...prev,
                        [barcode]: !prev[barcode],
                      }))
                    }
                  />
                </div>
              ))}
            </div>
          )}

          {/* Mall Table Extra Fields */}
          {item.category === "Mall Table" && (
            <div className="mt-4 space-y-2">
              <label>Arrival Time</label>
              <input
                type="text"
                className="w-full bg-gray-700 p-2 rounded"
                value={formFields.arrivalTime}
                readOnly
              />

              <label>Arrival Status</label>
              <select
                value={formFields.arrivalStatus}
                onChange={(e) => setFormFields({ ...formFields, arrivalStatus: e.target.value })}
                className="w-full bg-gray-700 p-2 rounded"
              >
                <option>On-Time</option>
                <option>Late</option>
                <option>No Show</option>
              </select>

              <label>Organization</label>
              <input
                className="w-full bg-gray-700 p-2 rounded"
                value={formFields.org}
                onChange={(e) => setFormFields({ ...formFields, org: e.target.value })}
              />

              <label>Tabling Spot</label>
              <input
                className="w-full bg-gray-700 p-2 rounded"
                value={formFields.tablingSpot}
                onChange={(e) => setFormFields({ ...formFields, tablingSpot: e.target.value })}
              />

              <label>Event #</label>
              <input
                className="w-full bg-gray-700 p-2 rounded"
                value={formFields.eventNumber}
                onChange={(e) => setFormFields({ ...formFields, eventNumber: e.target.value })}
              />

              <label>Number of Chairs</label>
              <select
                value={formFields.chairCount}
                onChange={(e) => {
                  const count = parseInt(e.target.value);
                  setFormFields({ ...formFields, chairCount: count });
                  setMallChairBarcodes(Array(count).fill(""));
                }}
                className="w-full bg-gray-700 p-2 rounded"
              >
                <option value="0">No Chairs</option>
                <option value="1">1 Chair</option>
                <option value="2">2 Chairs</option>
              </select>

              {[...Array(formFields.chairCount)].map((_, i) => (
                <input
                  key={i}
                  className="w-full bg-gray-700 p-2 rounded mt-2"
                  placeholder={`Scan Chair ${i + 1} Barcode`}
                  value={mallChairBarcodes[i] || ""}
                  onChange={(e) => {
                    const newCodes = [...mallChairBarcodes];
                    newCodes[i] = e.target.value;
                    setMallChairBarcodes(newCodes);
                  }}
                />
              ))}
            </div>
          )}

          <button
            onClick={handleCheckOut}
            className="mt-6 bg-green-600 px-4 py-2 rounded"
          >
            Check Out
          </button>
        </div>
      )}
    </div>
  );
}

export default ScanPage;
