import React, { useEffect, useState } from "react";
import axios from "axios";

function ManageUsers() {
  const [users, setUsers] = useState([]);
  const [activeUser, setActiveUser] = useState(null);
  const [formData, setFormData] = useState({ name: "", email: "", password: "", role: "user" });
  const [status, setStatus] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("Failed to load users", err);
      setStatus("❌ Failed to load users");
    }
  };

  const openModal = (user = null) => {
    setActiveUser(user);
    if (user) {
      setFormData({ name: user.name, email: user.email, password: "", role: user.role });
    } else {
      setFormData({ name: "", email: "", password: "", role: "user" });
    }
    setIsModalOpen(true);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (activeUser) {
        await axios.put(`http://localhost:5000/api/users/${activeUser._id}`, formData);
        setStatus("✅ User updated");
      } else {
        await axios.post("http://localhost:5000/api/auth/register", formData);
        setStatus("✅ User created");
      }
      fetchUsers();
      setIsModalOpen(false);
    } catch (err) {
      console.error(err);
      setStatus("❌ Failed to submit user");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      setUsers(users.filter((user) => user._id !== id));
    } catch (err) {
      alert("Failed to delete user");
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Manage Users</h1>
        <button
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
          onClick={() => openModal()}
        >
          + Create User
        </button>
      </div>

      {status && <p className="text-red-400 mb-4">{status}</p>}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {users.map((user) => (
          <div key={user._id} className="bg-gray-800 p-4 rounded-xl shadow">
            <h2 className="text-xl font-semibold">{user.name}</h2>
            <p className="text-gray-300 text-sm">{user.email}</p>
            <p className="text-gray-400 text-sm capitalize mb-3">{user.role}</p>
            <div className="flex gap-2">
              <button
                onClick={() => openModal(user)}
                className="bg-yellow-500 px-3 py-1 rounded hover:bg-yellow-600"
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(user._id)}
                className="bg-red-600 px-3 py-1 rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-xl max-w-xl w-full">
            <h2 className="text-2xl font-bold mb-4">{activeUser ? "Edit User" : "Create User"}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Name"
                className="w-full p-2 rounded bg-gray-700 text-white"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                className="w-full p-2 rounded bg-gray-700 text-white"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                className="w-full p-2 rounded bg-gray-700 text-white"
                value={formData.password}
                onChange={handleChange}
                required={!activeUser}
              />
              <select
                name="role"
                className="w-full p-2 rounded bg-gray-700 text-white"
                value={formData.role}
                onChange={handleChange}
              >
                <option value="user">User</option>
                <option value="dev">Dev</option>
                <option value="admin">Admin</option>
              </select>
              <div className="flex justify-between">
                <button
                  type="submit"
                  className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
                >
                  {activeUser ? "Update User" : "Create User"}
                </button>
                <button
                  type="button"
                  className="bg-gray-500 hover:bg-gray-600 px-4 py-2 rounded"
                  onClick={() => setIsModalOpen(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ManageUsers;
