// src/pages/AdminGenerateCodes.jsx
import React, { useState } from "react";
import axios from "axios";

function AdminGenerateCodes() {
  const [type, setType] = useState("barcode");
const [quantity, setQuantity] = useState(10);
const [building, setBuilding] = useState("MU"); // ✅ Fix added
const [result, setResult] = useState([]);
const [loading, setLoading] = useState(false);


  const generateCodes = async () => {
    setLoading(true);
    try {
      const res = await axios.post("/api/barcodes/generate", {
        type,
        quantity,
        building,
      });
      setResult(res.data.generated);
    } catch (err) {
      console.error("Generation failed:", err);
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="p-6 text-white">
      <h2 className="text-2xl font-bold mb-4">Generate Barcodes or QR Codes</h2>
      <div className="flex items-center gap-4 mb-4">
        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="bg-gray-800 p-2 rounded"
        >
          <option value="barcode">Barcode</option>
          <option value="qrcode">QR Code</option>
        </select>
        <select
  value={building}
  onChange={(e) => setBuilding(e.target.value)}
  className="bg-gray-800 p-2 rounded"
>
  <option value="MU">Memorial Union (MU)</option>
  <option value="STPV">Student Pavilion (STPV)</option>
</select>

        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
          className="bg-gray-800 p-2 rounded w-20"
        />
        <button
          onClick={generateCodes}
          className="bg-yellow-500 px-4 py-2 rounded"
          disabled={loading}
        >
          {loading ? "Generating..." : "Generate"}
        </button>
      </div>

      {result.length > 0 && (
        <div>
          <h3 className="text-lg font-semibold mb-2">Generated Codes:</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
            {result.map((code) => (
              <div
                key={code}
                className="bg-gray-800 p-3 rounded shadow text-center"
              >
                <p className="mb-2 font-mono">{code}</p>
                {type === "qrcode" ? (
                  <img
                    src={`https://api.qrserver.com/v1/create-qr-code/?data=${code}&size=100x100`}
                    alt="QR Code"
                    className="mx-auto"
                  />
                ) : (
                  <>
                    <p className="text-xs mb-1">(Text only)</p>
                    <button
                      onClick={() => navigator.clipboard.writeText(code)}
                      className="text-blue-400 text-sm underline"
                    >
                      Copy
                    </button>
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminGenerateCodes;
