import React, { useEffect, useState } from "react";
import axios from "axios";

function ManageUsers() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({ name: "", email: "", password: "", role: "user" });
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(true);
  const [editingUserId, setEditingUserId] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/users");
      setUsers(response.data);
    } catch (err) {
      console.error("Error loading users:", err);
      setStatus("❌ Failed to load users");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      setUsers((prevUsers) => prevUsers.filter((user) => user._id !== id));
    } catch (err) {
      console.error("Error deleting user:", err);
      alert("Failed to delete user.");
    }
  };

  const handleEdit = (user) => {
    setFormData({ name: user.name, email: user.email, password: "", role: user.role });
    setEditingUserId(user._id);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingUserId) {
        await axios.put(`http://localhost:5000/api/users/${editingUserId}`, formData);
        setStatus("✅ User updated successfully");
        setEditingUserId(null);
      } else {
        await axios.post("http://localhost:5000/api/auth/register", formData);
        setStatus("✅ User created successfully");
      }
      setFormData({ name: "", email: "", password: "", role: "user" });
      fetchUsers();
    } catch (err) {
      console.error("User operation failed:", err);
      setStatus("❌ Failed to save user");
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">Manage Users</h1>

      {status && <p className="mb-4 text-sm text-yellow-400">{status}</p>}

      <form onSubmit={handleSubmit} className="mb-10 bg-gray-800 p-4 rounded-lg">
        <div className="grid md:grid-cols-2 gap-4">
          <input className="p-2 bg-gray-700 rounded" name="name" placeholder="Name" value={formData.name} onChange={(e) => setFormData({ ...formData, name: e.target.value })} required />
          <input className="p-2 bg-gray-700 rounded" type="email" name="email" placeholder="Email" value={formData.email} onChange={(e) => setFormData({ ...formData, email: e.target.value })} required />
          <input className="p-2 bg-gray-700 rounded" type="password" name="password" placeholder="Password" value={formData.password} onChange={(e) => setFormData({ ...formData, password: e.target.value })} required={!editingUserId} />
          <select className="p-2 bg-gray-700 rounded" name="role" value={formData.role} onChange={(e) => setFormData({ ...formData, role: e.target.value })}>
            <option value="user">User</option>
            <option value="dev">Developer</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button type="submit" className="mt-4 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded">
          {editingUserId ? "Update User" : "Create User"}
        </button>
      </form>

      {loading ? (
        <p className="text-gray-400">Loading users...</p>
      ) : (
        <table className="min-w-full bg-gray-800 rounded-lg">
          <thead>
            <tr className="text-left bg-gray-700">
              <th className="p-3">Name</th>
              <th className="p-3">Email</th>
              <th className="p-3">Role</th>
              <th className="p-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user._id} className="border-t border-gray-700 hover:bg-gray-700">
                <td className="p-3">{user.name}</td>
                <td className="p-3">{user.email}</td>
                <td className="p-3 capitalize">{user.role}</td>
                <td className="p-3 flex gap-2">
                  <button onClick={() => handleEdit(user)} className="bg-yellow-500 px-3 py-1 rounded">Edit</button>
                  <button onClick={() => handleDelete(user._id)} className="bg-red-600 px-3 py-1 rounded">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ManageUsers;
