// src/pages/ItemHistory.jsx
import React, { useEffect, useState, useCallback } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import TopBar from "../components/TopBar";

function ItemHistory() {
  const { barcode } = useParams();
  const [logs, setLogs] = useState([]);
  const [item, setItem] = useState(null);

  const fetchLogs = useCallback(async () => {
    try {
      const itemRes = await axios.get(`/api/items/${barcode}`);
      setItem(itemRes.data);

      const logRes = await axios.get(`/api/items/${barcode}/logs`);
      setLogs(logRes.data);
    } catch (err) {
      console.error("Error fetching logs or item:", err);
    }
  }, [barcode]);

  useEffect(() => {
    fetchLogs();
  }, [fetchLogs]);

  return (
    <>
      <TopBar />
      <div className="p-6 min-h-screen bg-gray-900 text-white">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">
          Item History
        </h2>

        {item && (
          <div className="mb-6 bg-gray-800 p-4 rounded shadow-md">
            <p className="mb-1"><strong>Name:</strong> {item.name}</p>
            <p className="mb-1"><strong>Barcode:</strong> {item.barcode}</p>
            <p><strong>Category:</strong> {item.category}</p>
          </div>
        )}

        <div className="overflow-x-auto">
          <table className="table-auto w-full text-sm text-left border border-gray-700">
            <thead className="bg-gray-700 text-gray-200">
              <tr>
                <th className="px-4 py-2">Date</th>
                <th className="px-4 py-2">Action</th>
                <th className="px-4 py-2">User</th>
                <th className="px-4 py-2">Room</th>
                <th className="px-4 py-2">Client</th>
              </tr>
            </thead>
            <tbody className="bg-gray-800">
              {logs.map((log, index) => (
                <tr key={index} className="border-t border-gray-700">
                  <td className="px-4 py-2">{new Date(log.timestamp).toLocaleString()}</td>
                  <td className="px-4 py-2">
                    {log.action === "check_in" ? (
                      <span className="text-green-400 font-semibold">Checked In</span>
                    ) : (
                      <span className="text-red-400 font-semibold">Checked Out</span>
                    )}
                  </td>
                  <td className="px-4 py-2">{log.user || "—"}</td>
                  <td className="px-4 py-2">{log.room || "—"}</td>
                  <td className="px-4 py-2">{log.clientName || "—"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default ItemHistory;
