import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

function ItemHistory() {
  const { barcode } = useParams();
  const navigate = useNavigate();
  const [logs, setLogs] = useState([]);
  const [itemName, setItemName] = useState("");

  useEffect(() => {
    fetchLogs();
  }, [barcode]);

  const fetchLogs = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/items/${barcode}/logs`);
      setLogs(res.data);

      // Optionally fetch item name
      const itemsRes = await axios.get(`http://localhost:5000/api/items?barcode=${barcode}`);
      if (itemsRes.data.length > 0) {
        setItemName(itemsRes.data[0].name);
      }
    } catch (err) {
      console.error("Failed to fetch item history", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <button onClick={() => navigate("/admin-dashboard/items")} className="text-blue-400 underline mb-4">
        ‚Üê Back to Items
      </button>
      <h1 className="text-3xl font-bold mb-4">Item History: {itemName || barcode}</h1>

      {logs.length === 0 ? (
        <p className="text-gray-400">No logs found for this item.</p>
      ) : (
        <div className="space-y-4">
          {logs.map((log, index) => (
            <div key={index} className="bg-gray-800 rounded-xl p-4 shadow">
              <p><span className="font-semibold">Action:</span> {log.action}</p>
              <p><span className="font-semibold">Timestamp:</span> {new Date(log.timestamp).toLocaleString()}</p>
              <p><span className="font-semibold">Handled By:</span> {log.user?.name || "Unknown"}</p>
              {log.room && <p><span className="font-semibold">Room:</span> {log.room}</p>}
              {log.clientName && <p><span className="font-semibold">Client:</span> {log.clientName}</p>}
              {log.notes && <p><span className="font-semibold">Notes:</span> {log.notes}</p>}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ItemHistory;
