// pages/AdminLookupCode.jsx
import React, { useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";

function AdminLookupCode() {
  const [query, setQuery] = useState("");
  const [item, setItem] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    try {
      const res = await axios.get(`/api/items?barcode=${query}`);
      if (res.data && res.data.length > 0) {
        setItem(res.data[0]);
        setError(null);
      } else {
        setItem(null);
        setError("Item not found");
      }
    } catch (err) {
      console.error("Search failed", err);
      setError("Error fetching item");
      setItem(null);
    }
  };

  const isMic = item?.category.toLowerCase().includes("mic");
  const qrUrl = `https://api.qrserver.com/v1/create-qr-code/?data=${item?.barcode}&size=150x150`;

  return (
    <>
      <TopBar />
      <div className="bg-gray-900 min-h-screen text-white p-6">
        <h2 className="text-3xl font-bold text-asuGold mb-6 text-center">Lookup Barcode / QR Code</h2>

        <div className="flex gap-2 mb-4">
          <input
            type="text"
            placeholder="Enter item name or barcode"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="px-3 py-2 rounded bg-gray-800 text-white w-80"
          />
          <button
            onClick={handleSearch}
            className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700"
          >
            Search
          </button>
        </div>

        {error && <p className="text-red-500">{error}</p>}

        {item && (
          <div className="bg-gray-800 p-4 rounded shadow max-w-xl">
            <p className="text-lg font-semibold mb-2">Item: {item.name}</p>
            <p className="mb-2">Barcode: <span className="font-mono">{item.barcode}</span></p>

            {isMic ? (
              <div className="mt-4">
                <p className="mb-1 font-semibold">QR Code:</p>
                <img src={qrUrl} alt="QR Code" className="border border-gray-600" />
              </div>
            ) : (
              <div className="mt-4">
                <p className="mb-1 font-semibold">Barcode Value:</p>
                <div className="flex items-center gap-2">
                  <span className="bg-gray-700 px-3 py-1 rounded font-mono">{item.barcode}</span>
                  <button
                    onClick={() => navigator.clipboard.writeText(item.barcode)}
                    className="bg-yellow-500 hover:bg-yellow-600 px-2 py-1 rounded text-black"
                  >
                    Copy
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </>
  );
}

export default AdminLookupCode;
