import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function ManageItems() {
  const [items, setItems] = useState([]);
  const [building, setBuilding] = useState("Memorial Union");
  const [category, setCategory] = useState("Mic");
  const [formData, setFormData] = useState({
    name: "",
    barcode: "",
    building: "Memorial Union",
    category: "Mic",
  });
  const [editingId, setEditingId] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [status, setStatus] = useState("");

  const categories = ["Mic", "Lavaliers", "HDMI Cable", "Tech Bag", "Clicker"];
  const buildings = ["Memorial Union", "Student Pavilion"];
  const navigate = useNavigate();

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/items");
      setItems(res.data);
    } catch (err) {
      console.error("❌ Failed to fetch items", err);
    }
  };

  const filteredItems = items.filter(
    (item) => item.building === building && item.category === category
  );

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");
    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/api/items/${editingId}`, formData);
      } else {
        await axios.post("http://localhost:5000/api/items", formData);
      }
      setShowModal(false);
      resetForm();
      fetchItems();
    } catch (err) {
      if (err.response?.status === 400 && err.response?.data?.message) {
        setStatus(err.response.data.message);
      } else {
        setStatus("❌ Failed to save item");
      }
    }
  };

  const resetForm = () => {
    setFormData({ name: "", barcode: "", building, category });
    setEditingId(null);
  };

  const handleEdit = (item) => {
    setFormData(item);
    setEditingId(item._id);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/items/${id}`);
      fetchItems();
    } catch (err) {
      console.error("❌ Failed to delete item", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">Item Management</h1>

      {/* Building & Category Selector */}
      <div className="flex gap-4 mb-6">
        {buildings.map((b) => (
          <button
            key={b}
            onClick={() => {
              setBuilding(b);
              setCategory("Mic");
            }}
            className={`px-4 py-2 rounded ${
              building === b ? "bg-blue-600" : "bg-gray-700"
            }`}
          >
            {b}
          </button>
        ))}
      </div>

      <div className="flex gap-4 mb-6">
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setCategory(cat)}
            className={`px-4 py-2 rounded ${
              category === cat ? "bg-yellow-500" : "bg-gray-700"
            }`}
          >
            {cat}
          </button>
        ))}
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="ml-auto bg-green-600 hover:bg-green-700 px-4 py-2 rounded"
        >
          + Add Item
        </button>
      </div>

      {status && <p className="text-red-400 mb-4">{status}</p>}

      {/* Item Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filteredItems.map((item) => (
          <div key={item._id} className="bg-gray-800 p-4 rounded-xl shadow">
            <h2 className="text-xl font-semibold">{item.name}</h2>
            <p className="text-sm">Barcode: {item.barcode}</p>
            <p className="text-sm">Building: {item.building}</p>
            <p className="text-sm">Category: {item.category}</p>
            <div className="flex flex-wrap gap-2 mt-2">
              <button
                onClick={() => navigate(`/admin-dashboard/items/${item.barcode}`)}
                className="bg-blue-500 px-3 py-1 rounded"
              >
                View History
              </button>
              <button onClick={() => handleEdit(item)} className="bg-yellow-500 px-3 py-1 rounded">
                Edit
              </button>
              <button onClick={() => handleDelete(item._id)} className="bg-red-600 px-3 py-1 rounded">
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-xl max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">{editingId ? "Edit Item" : "Add Item"}</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              <input
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Item Name"
                className="w-full p-2 bg-gray-700 rounded"
                required
              />
              <input
                name="barcode"
                value={formData.barcode}
                onChange={handleChange}
                placeholder="Barcode"
                className="w-full p-2 bg-gray-700 rounded"
                required
              />
              <select
                name="building"
                value={formData.building}
                onChange={handleChange}
                className="w-full p-2 bg-gray-700 rounded"
              >
                {buildings.map((b) => (
                  <option key={b}>{b}</option>
                ))}
              </select>
              <select
                name="category"
                value={formData.category}
                onChange={handleChange}
                className="w-full p-2 bg-gray-700 rounded"
              >
                {categories.map((c) => (
                  <option key={c}>{c}</option>
                ))}
              </select>
              <div className="flex justify-end gap-4">
                <button type="submit" className="bg-green-600 px-4 py-2 rounded">Save</button>
                <button type="button" onClick={() => setShowModal(false)} className="bg-gray-500 px-4 py-2 rounded">Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default ManageItems;
