import React, { useEffect, useState } from "react";
import axios from "axios";

function ManageUsers() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({ name: "", email: "", password: "", role: "user" });
  const [status, setStatus] = useState("");
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("Failed to load users", err);
      setStatus("‚ùå Failed to load users");
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCreate = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", formData);
      setFormData({ name: "", email: "", password: "", role: "user" });
      setStatus("‚úÖ User created successfully");
      fetchUsers();
    } catch (err) {
      console.error("Failed to create user", err);
      setStatus("‚ùå Failed to create user");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/users/${id}`);
      setStatus("üóëÔ∏è User deleted");
      fetchUsers();
    } catch (err) {
      console.error("Delete failed", err);
      setStatus("‚ùå Failed to delete user");
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Manage Users</h1>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded shadow"
        >
          {showForm ? "Close Form" : "+ Create User"}
        </button>
      </div>

      {status && <p className="mb-4 text-sm">{status}</p>}

      {showForm && (
        <form onSubmit={handleCreate} className="bg-gray-800 p-6 rounded-lg mb-8 grid gap-4 max-w-md">
          <input
            className="bg-gray-700 p-2 rounded"
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
          />
          <input
            className="bg-gray-700 p-2 rounded"
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
          />
          <input
            className="bg-gray-700 p-2 rounded"
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />
          <select
            name="role"
            className="bg-gray-700 p-2 rounded"
            value={formData.role}
            onChange={handleChange}
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
            <option value="dev">Dev</option>
          </select>
          <button className="bg-green-600 hover:bg-green-700 p-2 rounded" type="submit">
            Create User
          </button>
        </form>
      )}

      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {users.map((user) => (
          <div key={user._id} className="bg-gray-800 p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-1">{user.name}</h2>
            <p className="text-sm text-gray-300 mb-1">{user.email}</p>
            <p className="text-sm text-gray-400 mb-3 capitalize">{user.role}</p>
            <div className="flex gap-2">
              <button className="bg-blue-500 px-3 py-1 rounded hover:bg-blue-600">View</button>
              <button className="bg-yellow-500 px-3 py-1 rounded hover:bg-yellow-600">Edit</button>
              <button
                onClick={() => handleDelete(user._id)}
                className="bg-red-600 px-3 py-1 rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ManageUsers;
