// src/pages/MyProfile.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";

function MyProfile() {
  const [oldPassword, setOldPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);
  const [loginHistory, setLoginHistory] = useState([]);
  const [notifications, setNotifications] = useState(true);

  useEffect(() => {
    // Fetch login history from backend (optional future setup)
    axios
      .get("/api/users/login-history", {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      })
      .then((res) => setLoginHistory(res.data))
      .catch((err) => console.error("Login history error:", err));
  }, []);

  const handlePasswordUpdate = async (e) => {
    e.preventDefault();
    setMessage(null);
    setError(null);

    if (newPassword !== confirmPassword) {
      return setError("New passwords do not match.");
    }

    if (newPassword.length < 8) {
      return setError("Password must be at least 8 characters.");
    }

    try {
      const res = await axios.post(
        "/api/users/change-password",
        { oldPassword, newPassword },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      setMessage("Password updated successfully.");
      setOldPassword("");
      setNewPassword("");
      setConfirmPassword("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to update password.");
    }
  };

  const toggleNotifications = async () => {
    try {
      const updated = !notifications;
      await axios.put(
        "/api/users/preferences",
        { notifications: updated },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setNotifications(updated);
    } catch (err) {
      console.error("Failed to toggle notifications", err);
    }
  };

  return (
    <>
      <TopBar />
      <div className="p-6 text-white min-h-screen bg-gray-900">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">My Profile</h2>

        <div className="max-w-xl mx-auto">
          {message && <p className="text-green-400 mb-2">{message}</p>}
          {error && <p className="text-red-500 mb-2">{error}</p>}

          <form onSubmit={handlePasswordUpdate} className="space-y-4">
            <h3 className="text-xl font-semibold">Change Password</h3>
            <input
              type="password"
              placeholder="Old Password"
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
              className="w-full p-2 bg-gray-800 border border-gray-600 rounded"
              required
            />
            <input
              type="password"
              placeholder="New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="w-full p-2 bg-gray-800 border border-gray-600 rounded"
              required
            />
            <input
              type="password"
              placeholder="Confirm New Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              className="w-full p-2 bg-gray-800 border border-gray-600 rounded"
              required
            />
            <button
              type="submit"
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
            >
              Update Password
            </button>
          </form>

          <div className="mt-8">
            <h3 className="text-xl font-semibold mb-2">Login Activity</h3>
            <ul className="bg-gray-800 p-4 rounded space-y-2 text-sm">
              {loginHistory.length > 0 ? (
                loginHistory.map((entry, i) => (
                  <li key={i} className="flex justify-between border-b border-gray-600 pb-1">
                    <span>{new Date(entry.timestamp).toLocaleString()}</span>
                    <span className="text-gray-400">{entry.location || "Unknown"}</span>
                  </li>
                ))
              ) : (
                <li className="text-gray-400">No recent login history available.</li>
              )}
            </ul>
          </div>

          <div className="mt-8">
            <h3 className="text-xl font-semibold mb-2">Email Notifications</h3>
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={notifications}
                onChange={toggleNotifications}
              />
              <span className="text-gray-300">Enable email alerts</span>
            </label>
          </div>
        </div>
      </div>
    </>
  );
}

export default MyProfile;
